import { Component, Input, OnInit } from '@angular/core';
import { faDroplet, faTemperatureHigh, faTemperatureLow, faWind } from '@fortawesome/free-solid-svg-icons';
import { WeatherDatas } from 'src/app/models/interfaces/WeatherDatas';

@Component({
  selector: 'app-weather-card',
  templateUrl: './weather-card.component.html',
  styleUrls: []
})
export class WeatherCardComponent{
  // Dados da previsão do tempo recebidos do componente pai
  @Input() weatherDatasInput!: WeatherDatas;

  minTemperatureIcon = faTemperatureLow;
  maxTemperatureIcon = faTemperatureHigh;
  humidityIcon = faDroplet;
  windIcon = faWind;
}
<div class="container" *ngIf="weatherDatasInput">
  <div class="upper-data">
    <img src="../../../../../assets/sun.jpg"
    *ngIf="weatherDatasInput.main.temp > 15"/>
    />
    <img src="../../../../../assets/cold1.jpg"
    *ngIf="weatherDatasInput.main.temp <= 15"/>
  </div>

  <div class="lower-data">
    <div class="more-info-label">Mais Informações</div>

    <div class="more-info-container">
      <div class="info-block">
        <div class="info-block-label">
          <fa-icon
          size="lg" [icon]="minTemperatureIcon"></fa-icon>
          <span>Mínima</span>
        </div>
      </div>
    </div>

  </div>

</div>
